import React, { useState } from 'react';
function LocationData(onClick) {
    const [cepData, setCepData] = useState(null);
    const [mostrarDados, setMostrarDados] = useState(false);

    const fetchCepData = async () => {
        const response = await fetch('https://viacep.com.br/ws/95560000/json/');
        if (response.ok) {
            const data = await response.json();
            setCepData(data);
            setMostrarDados(true);
        } else {
            setMostrarDados(false);
        }
    };

    const handleClickLocalizacao = () => {
        if (!mostrarDados) {
            fetchCepData();
        } else {
            setMostrarDados(!mostrarDados);
        }
    };

    return (
        <div className="dados">
            <button type="button" className="location" onClick={onClick}>Localização</button>

            <div className="dados-container">
                <div className="dados-row">
                    <div>
                        <p className='dados-p'>CEP: {cepData.cep}</p>
                    </div>
                    <div>
                        <p className='dados-p'>Cidade: {cepData.localidade}</p>
                    </div>
                </div>
                <div className="dados-row">
                    <div>
                        <p className='dados-p'>Rua: {"Av. Castelo Branco"}</p>
                    </div>
                    <div>
                        <p className='dados-p'>Número: {"1187"}</p>
                    </div>
                </div>
                <div className="dados-row">
                    <div>
                        <p className='dados-p'>Bairro: {"Centro"}</p>
                    </div>
                    <div>
                        <p className='dados-p'>Horário: {"20:00H"}</p>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default LocationData
