import React, { useState } from 'react';
import ButtonLocation from "./ButtonLocation";
import Header from "./Header";
import Intro from "./Intro";
import { info } from "../info"

function Gallery() {

    const [cepData, setCepData] = useState(null);

    // Função para lidar com a solicitação do CEP
    const fetchCepData = async () => {
        const response = await fetch('https://viacep.com.br/ws/95560000/json/');
        if (response.ok) {
            const data = await response.json();
            setCepData(data);
        } else {
            console.error('Erro ao buscar os dados do CEP');
        }
    };

    return (
    <div>
        <Header />
        <Intro name={"Gustavo"} />
        <ButtonLocation onClick={fetchCepData} />
        {cepData && (
            <div className="data-container">
                <div>
                    <p><strong>CEP:</strong> {cepData.cep}</p>
                    <p><strong>CIDADE:</strong> {cepData.localidade}</p>
                </div>
                <div className="data-row-2">
                    <p><strong>BAIRRO:</strong> {"São Jorge"}</p>
                    <p><strong>AV/RUA:</strong> {"Honorio Germano"}</p>
                </div>
                <div className="data-row">
                    <p><strong>HORÁRIO:</strong> {"20:00H"}</p>
                </div>
            </div>
        )}
    </div>
  );
}

export default Gallery;
