import React, { useState } from 'react';
import ButtonLocation from "./ButtonLocation";
import Header from "./Header";
import Intro from "./Intro";

function Gallery() {
  const [cepData, setCepData] = useState(null);

  // Função para lidar com a solicitação do CEP
  const fetchCepData = async () => {
    try {
      const response = await fetch('https://viacep.com.br/ws/95560000/json/');
      if (response.ok) {
        const data = await response.json();
        setCepData(data);
      } else {
        console.error('Erro ao buscar os dados do CEP');
      }
    } catch (error) {
      console.error('Erro na solicitação:', error);
    }
  };

  return (
    <div>
      <Header />
      <Intro name={"Gustavo"} />
      <ButtonLocation description={DA.description} />
      {cepData && (
        <div>
          <p>CEP: {cepData.cep}</p>
          <p>CIDADE: {cepData.localidade}</p>
          <p>BAIRRO: {cepData.bairro}</p>
          <p>AV/RUA: {cepData.logradouro}</p>
          <p>HORÁRIO: {"20:00H"}</p>
        </div>
      )}
    </div>
  );
}

export default Gallery;
