import React from 'react';

function Forms({ nome, telefone, status, pago, srcImg, onCadastro }) {
  const handleCadastro = () => {
    const egressoConvidado = status ? 'Egresso/Convidado' : 'Estudante';
    const confirmado = pago ? 'Confirmado' : 'Não Confirmado';

    const novaPessoa = {
      nome,
      telefone,
      egresso: egressoConvidado,
      pago: confirmado,
      fotoURL: srcImg,
    };

    onCadastro(novaPessoa);
  };

  return (
    <div className="forms-container">
      <h2>Formulário</h2>
      <form>
        <div>
          <label>Nome:</label>
          <input type="text" value={nome} required readOnly />
        </div>
        <div>
          <label>Telefone:</label>
          <input type="text" value={telefone} required readOnly />
        </div>
        <div>
          <label>Egresso/Convidado:</label>
          <input type="text" value={status ? 'Sim' : 'Não'} required readOnly />
        </div>
        <div>
          <label>Pago:</label>
          <input type="text" value={pago ? 'Sim' : 'Não'} required readOnly />
        </div>
        <div>
          <label>Foto (URL):</label>
          <input type="url" value={srcImg} required readOnly />
        </div>
        <button type="button" onClick={handleCadastro}>
          Cadastrar
        </button>
      </form>
    </div>
  );
}

export default Forms;
