import React, { useState } from 'react';
import ButtonLocation from "./ButtonLocation";
import Header from "./Header";
import Intro from "./Intro";

function Gallery() {
  const [cepData, setCepData] = useState(null);

  // Função para lidar com a solicitação do CEP
    const fetchCepData = async () => {
      const response = await fetch('https://viacep.com.br/ws/95560000/json/');
      if (response.ok) {
        const data = await response.json();
        setCepData(data);
      } else {
        console.error('Erro ao buscar os dados do CEP');
      }
  };

  return (
    <div>
      <Header />
      <Intro name={"Gustavo"} />
      <ButtonLocation onClick={fetchCepData} />
      {cepData && (
        <div>
          <p>CEP: {cepData.cep}</p>
          <p>CIDADE: {cepData.localidade}</p>
          <p>BAIRRO: {cepData.bairro}</p>
          <p>AVENIDA: {cepData.logradouro}</p>
          <p>HORÁRIO: {new Date().toLocaleTimeString()}</p>
        </div>
      )}
    </div>
  );
}

export default Gallery;
