import React, { useState } from 'react';

function Forms({ onCadastro }) {
  const [nome, setNome] = useState('');
  const [telefone, setTelefone] = useState('');
  const [egresso, setEgresso] = useState(false);
  const [pago, setPago] = useState(false);
  const [fotoURL, setFotoURL] = useState('');

  const handleCadastro = () => {
    const egressoConvidado = egresso ? 'Egresso/Convidado' : 'Estudante';
    const confirmado = pago ? 'Confirmado' : 'Não Confirmado';
    
    const novaPessoa = {
      nome,
      telefone,
      egresso: EgressoConvidado,
      pago: confirmado,
      fotoURL
    };

    onCadastro(novaPessoa);

    // Limpar os campos após o cadastro
    setNome('');
    setTelefone('');
    setEgresso(false);
    setPago(false);
    setFotoURL('');
  };

  return (
    <div className="forms-container">
      <h2>Formulário</h2>
      <form>
        <div>
          <label>Nome:</label>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Telefone:</label>
          <input
            type="text"
            value={telefone}
            onChange={(e) => setTelefone(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Egresso/Convidado:</label>
          <select
            value={egresso}
            onChange={(e) => setEgresso(e.target.value === 'true')}
          >
            <option value="true">Sim</option>
            <option value="false">Não</option>
          </select>
        </div>
        <div>
          <label>Pago:</label>
          <select
            value={pago}
            onChange={(e) => setPago(e.target.value === 'true')}
          >
            <option value="true">Sim</option>
            <option value="false">Não</option>
          </select>
        </div>
        <div>
          <label>Foto (URL):</label>
          <input
            type="url"
            value={fotoURL}
            onChange={(e) => setFotoURL(e.target.value)}
          />
        </div>
        <button type="button" onClick={handleCadastro}>
          Cadastrar
        </button>
      </form>
    </div>
  );
}

export default Forms;
