import React, { useState } from 'react';
import ButtonLocation from "./ButtonLocation";
import Header from "./Header";
import Intro from "./Intro";
import { info } from "../info"

function Gallery() {

    const [cepData, setCepData] = useState(null);

    // Função para lidar com a solicitação do CEP
    const fetchCepData = async () => {
        const response = await fetch('https://viacep.com.br/ws/95560000/json/');
        if (response.ok) {
            const data = await response.json();
            setCepData(data);
        } else {
            console.error('Erro ao buscar os dados do CEP');
        }
    };

    return (
    <div>
        <Header />
        <Intro name={"Gustavo"} />
        <ButtonLocation onClick={fetchCepData} />
        {cepData && (
        <div className="data-container">
          <div className="data-row">
            <p><strong>CEP:</strong></p>
            <p style={{ marginLeft: '20px' }}>{cepData.cep}</p>
          </div>
          <div className="data-row">
            <p><strong>CIDADE:</strong></p>
            <p>{cepData.localidade}</p>
          </div>
          <div className="data-row">
            <p><strong>BAIRRO:</strong></p>
            <p style={{ marginLeft: '20px' }}>{cepData.bairro}</p>
          </div>
          <div className="data-row">
            <p><strong>AV/RUA:</strong></p>
            <p>{cepData.logradouro}</p>
          </div>
          <div className="data-row">
            <p><strong>HORÁRIO:</strong></p>
            <p>{new Date().toLocaleTimeString()}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default Gallery;
