import React, { useState } from 'react';
import Address from "./Address";
import ButtonLocation from "./ButtonLocation"; // Importe o componente ButtonLocation
import Header from "./Header";
import Intro from "./Intro";

function Gallery() {
  const [cep, setCep] = useState(''); // Estado para armazenar o CEP
  const [addressData, setAddressData] = useState(null);
  const [showAddressInfo, setShowAddressInfo] = useState(false);

  const handleCepSubmit = async () => {
    try {
      const response = await fetch(`https://viacep.com.br/ws/${cep}/json/`);
      if (response.ok) {
        const data = await response.json();
        setAddressData(data);
        setShowAddressInfo(true);
      } else {
        console.error('Erro na busca do CEP');
      }
    } catch (error) {
      console.error('Erro ao buscar o CEP', error);
    }
  };

  return (
    <div>
      <Header />
      <Intro name={"Gustavo"} />
      <ButtonLocation onClick={handleCepSubmit} /> {/* Passe a função handleCepSubmit como prop "onClick" */}
      <Address cep={cep} setCep={setCep} />
    </div>
  );
}

export default Gallery;
