import React, { useState } from 'react';
import Header from "./Header";
import Welcome from "./Welcome";
import Location from "./Location";
import Forms from './Forms';
import Cards from './Cards';

function Home() {
    const [cepData, setCepData] = useState(null);
    const [mostrarDados, setMostrarDados] = useState(false);
    const [usuarios, setUsuarios] = useState([]);

    const fetchCepData = async () => {
        const response = await fetch('https://viacep.com.br/ws/95560000/json/');
        if (response.ok) {
            const data = await response.json();
            setCepData(data);
            setMostrarDados(true);
        } else {
            setMostrarDados(false);
        }
    };

    const handleClickLocalizacao = () => {
        if (!mostrarDados) {
            fetchCepData();
        } else {
            setMostrarDados(!mostrarDados);
        }
    };

    const handleCadastro = (usuario) => {
        setUsuarios([...usuarios, usuario]);
    };

    return (
        <div>
            <Header />
            <Welcome name={"Leo"} />
            <Location onClick={handleClickLocalizacao} />

            {mostrarDados && cepData && (
                <div className="dados">
    
                <div className="dados-container">
                    <div className="dados-row">
                        <div>
                            <p className='dados-p'>CEP: {cepData.cep}</p>
                        </div>
                        <div>
                            <p className='dados-p'>Cidade: {cepData.localidade}</p>
                        </div>
                    </div>
                    <div className="dados-row">
                        <div>
                            <p className='dados-p'>Rua: {"Av. Castelo Branco"}</p>
                        </div>
                        <div>
                            <p className='dados-p'>Número: {"1187"}</p>
                        </div>
                    </div>
                    <div className="dados-row">
                        <div>
                            <p className='dados-p'>Bairro: {"Centro"}</p>
                        </div>
                        <div>
                            <p className='dados-p'>Horário: {"20:00H"}</p>
                        </div>
                    </div>
                </div>
            </div>
            )}

            <div className="home-container">
                <div className="cards-container">
                    <Cards usuarios={usuarios} />
                </div>
                <div className="forms-container">
                    <Forms onCadastro={handleCadastro} />
                </div>
            </div>
        </div>
    );
}

export default Home;
