import React, { useState } from 'react';
import ButtonLocation from "./ButtonLocation";
import Header from "./Header";
import Intro from "./Intro";
import './Gallery.css'; // Importe seu arquivo CSS aqui

function Gallery() {
  const [cepData, setCepData] = useState(null);
  const [mostrarDados, setMostrarDados] = useState(true); // Estado para controlar a visibilidade dos dados

  // Função para lidar com a solicitação do CEP
  const fetchCepData = async () => {
    try {
      const response = await fetch('https://viacep.com.br/ws/95560000/json/');
      if (response.ok) {
        const data = await response.json();
        setCepData(data);
        setMostrarDados(true); // Exibir os dados quando a resposta chegar
      } else {
        console.error('Erro ao buscar os dados do CEP');
        setMostrarDados(false); // Ocultar os dados em caso de erro
      }
    } catch (error) {
      console.error('Erro na solicitação:', error);
      setMostrarDados(false); // Ocultar os dados em caso de erro
    }
  };

  const toggleDados = () => {
    setMostrarDados(!mostrarDados); // Inverte o estado de visibilidade dos dados
  };

    return (
    <div>
        <Header />
        <Intro name={"Gustavo"} />
        <ButtonLocation onClick={fetchCepData} />
        {cepData && (
            <div className='dados'>
                <div>
                    <p><strong>CEP: {cepData.cep}</strong></p>
                    <p><strong>CIDADE: {cepData.localidade}</strong></p>
                    <p><strong>BAIRRO: {"São Jorge"}</strong></p>
                    <p><strong>AVENIDA: {"Honorio Germano"}</strong></p>
                    <p><strong>HORÁRIO: {"20:00H"}</strong></p>
                </div>
            </div>
        )}
    </div>
  );
}

export default Gallery;
