function ButtonLocation({description}) {
    const [enable, setEnable] = useState(false)

    function handleClickEnableDetails() {
        setEnable(!enable)
    }

    return(
        <div>
            {enable && <p>{description}</p>}
            <button className="button-location" onClick={handleClickEnableDetails}>{enable ? "Hide" : "Show"}</button>
        </div>
    )
}

export default ButtonLocation;
