import React, { useState } from 'react';
import ButtonLocation from "./ButtonLocation";
import Header from "./Header";
import Intro from "./Intro";
import { info } from "../info"

function Gallery() {

    const [cepData, setCepData] = useState(null);

    // Função para lidar com a solicitação do CEP
        const fetchCepData = async () => {
        const response = await fetch('https://viacep.com.br/ws/95560000/json/');
        if (response.ok) {
        const data = await response.json();
        setCepData(data);
        } else {
        console.error('Erro ao buscar os dados do CEP');
        }
    };

    return (
    <div>
        <Header />
        <Intro name={"Gustavo"} />
        <ButtonLocation onClick={fetchCepData} />
        {cepData && (
        <div className="dados">
            <p>CEP: {cepData.cep}</p>
            <p>CIDADE: {cepData.localidade}</p>
            <p>BAIRRO: {"São Jorge"}</p>
            <p>AVENIDA: {"Honorio Germano"}</p>
            <p>HORÁRIO: {"20:00H"}</p>
        </div>
        )}

        {cepData && (
            <div className="data-container">
                <div className="left-data">
                    <p><strong>CEP:</strong> {cepData.cep}</p>
                    <p><strong>BAIRRO:</strong> {cepData.bairro}</p>
                    <p><strong>HORÁRIO:</strong> {new Date().toLocaleTimeString()}</p>
                    </div>
                    <div className="right-data">
                    <p><strong>CIDADE:</strong> {cepData.localidade}</p>
                    <p><strong>AV/RUA:</strong> {cepData.logradouro}</p>
                </div>
            </div>
        )}
    </div>
  );
}

export default Gallery;
