import React from 'react';

function ButtonLocation({ onClick }) {

  const [cepData, setCepData] = useState(null);
  // Função para lidar com a solicitação do CEP
  const fetchCepData = async () => {
      const response = await fetch('https://viacep.com.br/ws/95560000/json/');
      if (response.ok) {
      const data = await response.json();
      setCepData(data);
      } else {
      console.error('Erro ao buscar os dados do CEP');
      }
  };
  return (
    <button type="button" className="button-location" onClick={onClick}>
      Localização
    </button>

    
  );
}

export default ButtonLocation;
