import React, { useState } from 'react';
import ButtonLocation from './ButtonLocation';

function Address({ cep, setCep }) { // Receba "cep" e "setCep" como props
  const [addressData, setAddressData] = useState(null);
  const [showAddressInfo, setShowAddressInfo] = useState(false);

  const handleCepSubmit = async () => {
    const response = await fetch(`https://viacep.com.br/ws/${cep}/json/`);
    const data = await response.json();
    setAddressData(data);
    setShowAddressInfo(true);
  }

  return (
    <div>
      {showAddressInfo && (
        <div>
          value={cep}
          onChange={(e) => setCep(e.target.value)} // Use a função setCep para atualizar o valor de cep
          <button onClick={handleCepSubmit}>Localização</button>
          {/* Exiba os dados do endereço aqui */}
          <p>CEP: {addressData.cep}</p>
          <p>Bairro: {addressData.bairro}</p>
          <p>Horario: {"20:00H"}</p>
          <p>Logradouro: {addressData.logradouro}</p>
          <p>Cidade: {addressData.localidade}</p>
          <p>Estado: {addressData.uf}</p>
        </div>
      )}
    </div>
  );
}

export default Address;
